    private void txtSearchClientKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtSearchClientKeyReleased
        try {
            if (!kon.isClosed()) {
                String query = "SELECT * FROM `clients` WHERE `name` like '%" + txtSearchClient.getText() + "%'";
                pst = kon.prepareStatement(query);
                //pst.setString(1,txt_search.getText());
                rs = pst.executeQuery();
                tbl_client.setModel(DbUtils.resultSetToTableModel(rs));
                //table();
            } else {
                kon.close();
            }
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Error:: \n" + e);
        }
    }//GEN-LAST:event_txtSearchClientKeyReleased

